import React, { Component } from 'react';
import quotes from './quotesDB';
import colors from './colorsDB';

class App extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        quote: quotes[0].quote,
        author: quotes[0].author,
        color: colors[0]
      }
       this.generateRandomQuotes = this.generateRandomQuotes.bind(this);
    }
    // generate new quote
    generateRandomQuotes() {
      let num1 = Math.floor(Math.random() * quotes.length);
      let num2 = Math.floor(Math.random() * colors.length);
      this.setState({
        quote: quotes[num1].quote,
        author: quotes[num1].author,
        color: colors[num2]
      })
    }

    render() {
      const style = {
        backgroundColor: this.state.color,
        color: this.state.color
      }

      return (
        <div className="container" style={style}>
          <div className="container_contain">
            <div className="container_contain-quote">{this.state.quote}</div> 
            <div className="container_contain-author">- {this.state.author}</div> 
            <div className="container_contain-button">
              <div id="left_button">
                <button id="first_button"><i class="fa-brands fa-twitter"></i></button>
                <button id="second_button"><i class="fa-brands fa-tumblr"></i></button>
              </div>
              <div id="right_button">
                <button id="third_button" type="submit" onClick = {this.generateRandomQuotes}>New quote</button>
              </div>
            </div> 
          </div>
          <div className="container_author" style={{color: "white"}}>by Minh Duc Nguyen</div>
        </div> 
      );
    }
  }

  export default App;
