import { GENERATE } from "./generateQuote.type";
import quotes from './quotesDB';
import colors from './colorsDB';

const initial_state = {
     quote: quotes[0].quote,
     author: quotes[0].author,
     color: colors[0]
};

const generateReducer = (state = initial_state, action) => {
     switch (action.type) {
          case GENERATE: 
             return {
               ...state, 
               quote: quotes[Math.floor(Math.random() * quotes.length)].quote, 
               author: quotes[Math.floor(Math.random() * quotes.length)].author, 
               color: colors[Math.floor(Math.random() * colors.length)]
             };
          default: 
             return state;
     }
}

export default generateReducer;
